import React, { useState, useRef, useCallback } from 'react';
import { createClient, LiveTranscriptionEvents } from '@deepgram/sdk';
import axios from 'axios';
import AuraVisualization from './components/AuraVisualization';
import TranscriptDisplay from './components/TranscriptDisplay';
import KeywordsDisplay from './components/KeywordsDisplay';
import Controls from './components/Controls';
import './styles/App.css';

const DEEPGRAM_API_KEY = process.env.REACT_APP_DEEPGRAM_API_KEY;
console.log('Deepgram API Key:', DEEPGRAM_API_KEY); // Debug line
const BACKEND_URL = 'http://localhost:8000';

function App() {
  // State management
  const [isRecording, setIsRecording] = useState(false);
  const [transcript, setTranscript] = useState([]);
  const [currentText, setCurrentText] = useState('');
  const [sentiment, setSentiment] = useState(0);
  const [keywords, setKeywords] = useState([]);
  const [emotion, setEmotion] = useState('neutral');
  const [energy, setEnergy] = useState(0.5);
  const [colorPalette, setColorPalette] = useState(['#4A90E2', '#50C878', '#FFB347']);
  const [isProcessing, setIsProcessing] = useState(false);

  // Refs for media handling
  const mediaRecorderRef = useRef(null);
  const deepgramRef = useRef(null);
  const streamRef = useRef(null);

  // Process text with backend AI
  const processText = useCallback(async (text) => {
    if (!text.trim() || isProcessing) return;

    console.log('Processing text with backend:', text); // Debug line
    setIsProcessing(true);
    try {
      const response = await axios.post(`${BACKEND_URL}/process_text`, {
        text: text,
        provider: 'openai' // or 'anthropic'
      });

      const data = response.data;
      console.log('Backend response:', data); // Debug line
      setSentiment(data.sentiment);
      setKeywords(data.keywords);
      setEmotion(data.emotion);
      setEnergy(data.energy);
      setColorPalette(data.color_palette);
    } catch (error) {
      console.error('Error processing text:', error);
    } finally {
      setIsProcessing(false);
    }
  }, [isProcessing]);

  // Start recording and transcription
  const startRecording = useCallback(async () => {
    console.log('Starting recording...'); // Debug line
    try {
      // Get microphone access
      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: { 
          sampleRate: 16000,
          channelCount: 1,
          echoCancellation: true,
          noiseSuppression: true
        } 
      });
      console.log('Got microphone access:', stream); // Debug line
      streamRef.current = stream;

      // Initialize Deepgram client
      const deepgram = createClient(DEEPGRAM_API_KEY);
      const dgConnection = deepgram.listen.live({
        model: 'nova-2',        // Back to basic model
        language: 'en-US',      // Back to specific locale
        interim_results: false,  // Turn off interim results
        smart_format: false,    // Turn off smart formatting
        punctuate: false        // Turn off punctuation
      });
      console.log('Created Deepgram connection'); // Debug line

      deepgramRef.current = dgConnection;

      // Set up Deepgram event listeners
      dgConnection.on(LiveTranscriptionEvents.Open, () => {
        console.log('Deepgram connection opened');
        // Test connection readiness
        console.log('Connection ready state:', dgConnection.getReadyState());
      });

      dgConnection.on(LiveTranscriptionEvents.Transcript, (data) => {
        console.log('Received transcript data:', data); // Debug line
        const transcript = data.channel.alternatives[0].transcript;
        console.log('Extracted transcript:', transcript); // Debug line

        if (transcript && transcript.trim() !== '') {
          console.log('Setting current text:', transcript); // Debug line
          setCurrentText(transcript);

          // If this is a final transcript, add to history and process
          if (data.is_final) {
            console.log('Final transcript, adding to history:', transcript); // Debug line
            setTranscript(prev => [...prev, transcript]);
            processText(transcript);
            setCurrentText('');
          }
        }
      });

      dgConnection.on(LiveTranscriptionEvents.Error, (error) => {
        console.error('Deepgram error:', error);
      });

      dgConnection.on(LiveTranscriptionEvents.Close, () => {
        console.log('Deepgram connection closed'); // Debug line
      });

      // Set up MediaRecorder to stream audio to Deepgram
      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: 'audio/webm' // Removed codec specification
      });
      console.log('Created MediaRecorder:', mediaRecorder); // Debug line

      mediaRecorderRef.current = mediaRecorder;

      mediaRecorder.ondataavailable = (event) => {
        console.log('MediaRecorder data available, size:', event.data.size); // Debug line
        if (event.data.size > 0 && dgConnection.getReadyState() === 1) {
          console.log('Sending data to Deepgram'); // Debug line
          dgConnection.send(event.data);
        } else {
          console.log('Not sending data - size:', event.data.size, 'readyState:', dgConnection.getReadyState()); // Debug line
        }
      };

      mediaRecorder.start(100); // Send data every 100ms
      console.log('MediaRecorder started'); // Debug line
      setIsRecording(true);

    } catch (error) {
      console.error('Error starting recording:', error);
      alert('Error accessing microphone. Please check permissions.');
    }
  }, [processText]);

  // Stop recording and cleanup
  const stopRecording = useCallback(() => {
    console.log('Stopping recording...'); // Debug line
    if (mediaRecorderRef.current) {
      mediaRecorderRef.current.stop();
    }

    if (deepgramRef.current) {
      deepgramRef.current.finish();
    }

    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
    }

    setIsRecording(false);
    setCurrentText('');
  }, []);

  // Clear all data
  const clearData = useCallback(() => {
    console.log('Clearing all data'); // Debug line
    setTranscript([]);
    setCurrentText('');
    setSentiment(0);
    setKeywords([]);
    setEmotion('neutral');
    setEnergy(0.5);
    setColorPalette(['#4A90E2', '#50C878', '#FFB347']);
  }, []);

  return (
    <div className="app">
      {/* Background Aura Visualization */}
      <AuraVisualization 
        sentiment={sentiment}
        energy={energy}
        emotion={emotion}
        colorPalette={colorPalette}
        keywords={keywords}
      />

      {/* UI Overlay */}
      <div className="ui-overlay">
        {/* Controls */}
        <Controls 
          isRecording={isRecording}
          isProcessing={isProcessing}
          onStart={startRecording}
          onStop={stopRecording}
          onClear={clearData}
        />

        {/* Transcript Display */}
        <TranscriptDisplay 
          transcript={transcript}
          currentText={currentText}
          sentiment={sentiment}
          emotion={emotion}
        />

        {/* Keywords Display */}
        <KeywordsDisplay 
          keywords={keywords}
          emotion={emotion}
          energy={energy}
        />

        {/* Status Info */}
        <div className="status-info">
          <div className="sentiment-meter">
            <div className="meter-label">Sentiment</div>
            <div className="meter-bar">
              <div 
                className="meter-fill" 
                style={{
                  width: `${((sentiment + 1) / 2) * 100}%`,
                  backgroundColor: sentiment > 0 ? '#4CAF50' : sentiment < 0 ? '#F44336' : '#FFC107'
                }}
              />
            </div>
            <div className="meter-value">{(sentiment * 100).toFixed(0)}%</div>
          </div>

          <div className="energy-meter">
            <div className="meter-label">Energy</div>
            <div className="meter-bar">
              <div 
                className="meter-fill" 
                style={{
                  width: `${energy * 100}%`,
                  backgroundColor: `hsl(${energy * 120}, 70%, 50%)`
                }}
              />
            </div>
            <div className="meter-value">{(energy * 100).toFixed(0)}%</div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
